(function(){tinymce.create('tinymce.plugins.IBrowserPlugin',{init:function(ed,url){var head=document.getElementsByTagName('head')[0];var script=document.createElement('script');script.type='text/javascript';script.src=url+'/interface/common.js';head.appendChild(script);ed.addCommand('mceIBrowser',function(){var e=ed.selection.getNode();if(ed.dom.getAttrib(e,'class').indexOf('mceItem')!=-1)return;ib.isMSIE=tinymce.isIE;ib.isGecko=tinymce.isGecko;ib.isWebKit=tinymce.isSafari;ib.oEditor=ed;ib.editor=ed;ib.selectedElement=e;ib.baseURL=url+'/ibrowser.php';iBrowser_open()});ed.addButton('ibrowser',{title:'Insert Image',cmd:'mceIBrowser',image:url+'/interface/images/tinyMCE/imgIcon.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('ibrowser',n.nodeName=='IMG')})},getInfo:function(){return{longname:'iBrowser',author:'net4visions.com',authorurl:'http://net4visions.com',infourl:'http://net4visions.com',version:'1.3.8'}}});tinymce.PluginManager.add('ibrowser',tinymce.plugins.IBrowserPlugin)})();